import React, { useState } from 'react';
import "../styles/RecipeItem.css"

function RecipeItem({ image, name, category, instructions }) {
  const [showMore, setShowMore] = useState(false);
  const [isFavorite, setIsFavorite] = useState(false);
  const [favorites, setFavorites] = useState([]);

  const handleToggle = () => {
    setShowMore(!showMore);
  };

  const handleFavorite = () => {
    setIsFavorite(!isFavorite);

    if (!isFavorite) {
      // Add the recipe to favorites
      setFavorites([...favorites, name]);
    } else {
      // Remove the recipe from favorites
      const updatedFavorites = favorites.filter((favorite) => favorite !== name);
      setFavorites(updatedFavorites);
    }
  };

  return (
    <div className="recipeItem">
      <div className="header">
        <img src={image} alt={name} />
        <h2>{name}</h2>
        <button className={`favoriteButton ${isFavorite ? 'active' : ''}`} onClick={handleFavorite}>
          {isFavorite ? '❤️' : '♡'}
        </button>
      </div>
      <p>Category: {category}</p>
      {showMore && <p>Instructions: {instructions}</p>}
      <button onClick={handleToggle}>
        {showMore ? 'Show Less' : 'See More'}
      </button>
      <div>
        <h3>Favorites:</h3>
        {favorites.length > 0 ? (
          <ul>
            {favorites.map((favorite, index) => (
              <li key={index}>{favorite}</li>
            ))}
          </ul>
        ) : (
          <p>No favorites selected.</p>
        )}
      </div>
    </div>
  );
}

export default RecipeItem;
