import React, { useEffect } from 'react';
import './navBar.css';

const NavBar = () => {
  useEffect(() => {
    const menuItems = document.querySelectorAll('.dropdown .nav-link');

    function onClick(e) {
      e.preventDefault();
      const el = this.parentNode;
      el.classList.contains('show-submenu') ? hideSubMenu(el) : showSubMenu(el);
    }

    function showSubMenu(el) {
      el.classList.add('show-submenu');
      document.addEventListener('click', onDocClick);
    }

    function hideSubMenu(el) {
      el.classList.remove('show-submenu');
      document.removeEventListener('click', onDocClick);
    }

    const onDocClick = (e) => {
      e.preventDefault();

      const el = document.querySelector('.dropdown.show-submenu');
      if (el && !el.contains(e.target)) {
        document.removeEventListener('click', onDocClick);
        hideSubMenu(el);
      }
    };

    menuItems.forEach((el) => {
      el.addEventListener('click', onClick, false);
    });

    return () => {
      menuItems.forEach((el) => {
        el.removeEventListener('click', onClick, false);
      });
      document.removeEventListener('click', onDocClick);
    };
  }, []);

  return (
    <div>
      <>
        {/* Developed by http://grohit.com/ */}
        <p data-item="COOKFOLIO">COOKFOLIO</p>
        <section>
          <div className="slogan">Redefining the way you cook</div>
          <div className="emoji">👩🏻‍🍳</div>
          <nav>
            <ul className="menuItems">
              <li>
                <a href="#" data-item="recettes">
                  recettes
                </a>
              </li>
              <li>
                <a href="#" data-item="videos">
                  videos
                </a>
              </li>
              <li>
                <a href="#" data-item="cuisiner">
                  cuisiner
                </a>
              </li>
              <li>
                <a href="#" data-item="contact us">
                  contact us
                </a>
              </li>
              <>
                <span>Products</span>
                <nav className="submenu">
                  <ul className="submenu-items">
                    <li className="submenu-item">
                      <a href="#" className="submenu-link">
                        Product #1
                      </a>
                    </li>
                    <li className="submenu-item">
                      <a href="#" className="submenu-link">
                        Product #2
                      </a>
                    </li>
                    <li className="submenu-item">
                      <a href="#" className="submenu-link">
                        Product #3
                      </a>
                    </li>
                  </ul>
                </nav>
              </>
            </ul>
          </nav>
        </section>
      </>
    </div>
  );
};

export default NavBar;
